-- QUICK FIX: Update match_products to handle text embeddings
-- Run this in Supabase SQL Editor
-- This fixes the search immediately without converting 20K rows

DROP FUNCTION IF EXISTS public.match_products(vector(1536), integer);
DROP FUNCTION IF EXISTS public.match_products(text, integer);

CREATE OR REPLACE FUNCTION public.match_products(
  query_embedding text,  -- Accept text instead of vector
  match_count int DEFAULT 20
)
RETURNS TABLE (
  id uuid,
  brand text,
  title text,
  description text,
  tags text[],
  price numeric(10,2),
  currency text,
  image_url text,
  product_url text,
  combined_text text,
  merchant_id text,
  merchant_name text,
  affiliate_network text,
  image_embedding text,
  similarity float
)
LANGUAGE sql STABLE AS $$
  SELECT
    p.id,
    p.brand,
    p.title,
    p.description,
    p.tags,
    p.price,
    p.currency,
    p.image_url,
    p.product_url,
    p.combined_text,
    p.merchant_id,
    p.merchant_name,
    p.affiliate_network,
    p.image_embedding,
    -- Cast both text embeddings to vector for comparison
    1 - (p.embedding::vector(1536) <-> query_embedding::vector(1536)) AS similarity
  FROM public.products p
  WHERE p.embedding IS NOT NULL
  ORDER BY p.embedding::vector(1536) <-> query_embedding::vector(1536)
  LIMIT match_count;
$$;

